import React, { Component } from "react";
import NewsItem from "./NewsItem";
export class News extends Component {
  articles = [{"source":{"id":null,"name":""},"author":"L","title":"","url":"","urlToImage":"https://images.livemint.com/img/2022/02/22/600x338/coronavirus_1608178200730_1608178208280_1645524726260.PNG","publishedAt":"2022-02-22T10:12:44Z","content":"In an astounding discovery regarding COVID-19, a recent study has shown that we have inherited major genetic factors from Neanderthals that can cause severe Covid infections. However, the same factor… [+2106 chars]"}]

  constructor() {
    super();
    this.state = {
      articles: this.articles,
      loading: false,
    };
  }

  async componentDidMount(){
      let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=67203caca5b14a1baceaaa38824f59f1"
      let data = await fetch(url)
      let parsedData = await data.json()
      this.setState({articles: parsedData.articles})
  }

  render() {
    return (
      <div className="container my-4">
        <h1>News Dose - Top Headlines</h1>
        <h7>-by Sameer Syed</h7>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title?element.title.slice(0,45):""}
                  description={element.description?element.description.slice(0,88):""}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default News;
